/* eslint-disable no-console */
import { inspect } from 'util'

export const log = _ => {
    console.log(_)
    return _
}

export const deep = _ => {
    console.log(
        inspect(_, { showHidden: false, depth: null, maxArrayLength: null })
    )
    return _
}

export const note = msg => _ => {
    log(msg)
    return _
}

log.deep = deep
log.note = note

export default log
