import describe from 'kape'
import * as _ from '@dev-xp/utils'

const value = { test: { first: 'pass' } }

describe('@dev-xp/utils', _.getProp, snapshot => {
    snapshot('_.getProp() normal', [
        ['test.first', value],
        ['test', value],
    ])

    snapshot('_.getProp() bad key/value', [
        ['', value],
        [{}, value],
        [undefined, value],
        ['', undefined],
        [{}, undefined],
        [undefined, undefined],
    ])
})
