import describe from 'kape'
import dedent from 'dedent'
import parse from '@entyre/parse-sfp'

describe('Parse Single File Package', parse, snapshot => {
    snapshot(
        [],
        [{ code: '' }],
        [
            {
                code: dedent`
        # Testing

        \`\`\`js
        console.log('hello')
        \`\`\`
    `,
            },
        ],
        [
            {
                code: dedent`
        # Testing

        \`\`\`js
        console.log('first')
        \`\`\`

        \`\`\`js
        console.log('second')
        \`\`\`
    `,
            },
        ],
        [
            {
                code: dedent`
        # Testing

        \`\`\`js
        console.log('first')
        \`\`\`

        ## Second header

        \`\`\`js
        console.log('second')
        \`\`\`
    `,
            },
        ]
    )
})
