export default (options = {}) => {
    const filters = []
    const { user, streamer } = options

    if (user !== undefined)
        filters.push(
            ({ userName }) =>
                user
                    ? userName.toLowerCase() === user.toLowerCase()
                    : userName.toLowerCase() !== user.toLowerCase()
        )

    if (streamer !== undefined)
        filters.push(
            ({ userRoles }) =>
                streamer
                    ? userRoles.includes('Owner')
                    : !userRoles.includes('Owner')
        )

    return msg => filters.reduce((result, next) => next(msg) && result, true)
}
