import * as jsdiff from 'diff'
import chalk from 'chalk'

export default (first, second) =>
    jsdiff
        .diffLines(first, second)
        .reduce(
            (result, part) =>
                part.added
                    ? chalk`${result}{green ${part.value}}`
                    : part.removed
                        ? chalk`${result}{red ${part.value}}`
                        : chalk`${result}{grey ${part.value}}`,
            ''
        )
