import { obj as stream } from 'through2';
import run from '@kape/run';

run(
    'Suite for a promise',
    str => Promise.resolve(str),
    it => {
        it('should return the string', given => given([['hello']]));
    },
).pipe(
    stream((item, enc, cb) => {
        console.log(item);
        cb();
    }),
);
