import { obj as stream } from 'through2'
import run from '@kape/run'

run(
    'Suite for a promise',
    str => Promise.resolve(str),
    snapshot => {
        snapshot(['hello'])
    }
).pipe(
    stream((item, enc, cb) => {
        // eslint-disable-next-line no-console
        console.log(item)
        cb()
    })
)
