import { resolve, dirname } from 'path'

import slugify from 'slugify'

export const key = ({ test, index }) => `${test} #${index}`
export const location = ({ suite, file }) => ({
    dir: resolve(dirname(file), '__snapshots__'),
    file: resolve(
        dirname(file),
        '__snapshots__',
        `${slugify(suite, { lower: true })}.js.snap`
    ),
})
