import collapse from '@kikd/collapse'

export default ({ plugins = {} }) => ({
    packageNames = [],
    rootManifest,
    dependencies,
    structures,
    manifests,
    types,
} = {}) =>
    packageNames
        .map(
            name =>
                types[name] !== 'unknown'
                    ? /* eslint-disable indent */
                      {
                          [name]:
                              plugins[types[name]].config({
                                  rootManifest,
                                  dependencies: dependencies[name],
                                  structure: structures[name],
                                  manifest: manifests[name],
                              }) || {},
                      }
                    : { [name]: {} }
        )
        .reduce(collapse, {})
