import { NOFN } from '@dev-xp/noop'

export default ({ plug = NOFN } = {}) => (
    name = '<undefined>',
    {
        types = {},
        rootManifest = {},
        dependencies = {},
        structures = {},
        manifests = {},
    } = {}
) => ({
    [name]: plug('config')({
        type: types[name],
        rootManifest,
        dependencies: dependencies[name],
        structure: structures[name],
        manifest: manifests[name],
    }),
})
