export default plugins => ({ packages = [], baseConfig, dependencies } = {}) =>
    [].concat(
        ...Object.entries(plugins)
            .filter(([, plugin]) => packages.some(plugin.filter))
            .map(([, plugin]) =>
                [].concat(
                    ...packages
                        .filter(plugin.filter)
                        .map(plugin.config({ baseConfig, dependencies }))
                )
            )
    )
