import parse from '@kikd/commit-parser';
import configureGit from 'simple-git/promise';

const git = configureGit();

// https://git-scm.com/docs/pretty-formats
export default (opts = {}) =>
    git
        .log({
            ...opts,
            format: {
                hash: '%H',
                message: '%B',
            },
        })
        .then(({ all }) => ({ commits: all }))
        .then(parse);
