import getPackages, { packageFilters } from '@kikd/command-list'
import publishCheck from '@kikd/publish-check'
import template from '@kikd/template-checked-packages'

export default function check(config) {
    return getPackages(config).extend('checks', publishCheck)
}

export const command = () => ({
    name: 'check',
    run: check,
    template,
    args: packageFilters,
})
