import getPackages from '@kikd/command-list';
import getDependencies from '@kikd/dependency-list';
import dependenciesTemplate from '@kikd/template-dependencies';

export default function dependencies(...args) {
    return getPackages(...args).extend('dependencies', getDependencies);
}

export const command = {
    name: 'dependencies',
    aliases: ['deps', 'dependents'],
    template: dependenciesTemplate,
    run: dependencies,
};
