/* eslint-disable no-console */
import { prompt } from 'inquirer'

export const preCreate = ({ name }) =>
    prompt([
        {
            type: 'input',
            name: 'executableName',
            message: 'What should the executable name be?',
            default: name,
        },
    ])

export default () => {
    console.log('Created CLI Tool')
}
