import { NOFN } from '@dev-xp/noop'

export default ({ plug = NOFN } = {}) => (
    name = '<undefined>',
    { types = {}, structures = {}, manifests = {} } = {}
) => ({
    [name]: plug('details')({
        type: types[name],
        structure: structures[name],
        manifest: manifests[name],
    }),
})
