import fs from 'fs';
import { resolve } from 'path';
import promisify from 'es6-promisify';

const lstat = promisify(fs.lstat);

export default ({ root = '.' } = {}) => new Promise(async (res, rej) => {
    try {
        const stats = await lstat(resolve(root, 'src/node_modules'));

        if (stats.isDirectory) return res([]);
        return rej('NO');
    } catch (e) {
        return rej('NO');
    }
});
