import fs from 'fs';
import { resolve } from 'path';
import promisify from 'es6-promisify';

const lstat = promisify(fs.lstat);

export default ({ root = '.' } = {}) => new Promise((res, rej) =>
    lstat(resolve(root, 'src/node_modules'))
        .then((stats) => {
            if (stats.isDirectory) return res([]);
            return rej('NO');
        })
        .catch(() => rej('NO')));
