import fs from 'fs';
import { resolve } from 'path';

export default ({ root = '.' } = {}) => new Promise((res, rej) => {
    fs.lstat(resolve(root, 'src/node_modules'), (err, stats) => {
        if (err) return rej('NO');
        if (stats.isDirectory) return res([]);
        return rej('NO');
    });
});
