export default ({ packages = [] } = {}) => `
    <bar>--------------------------------------------------------------------------------</bar>
    <h1>Package List</h1>
    <bar>--------------------------------------------------------------------------------</bar>
    <list>
        ${packages
            .map(
                pkg => `<item>
                <attr><key>Name:</key><value>${pkg.name}</value></attr>
                <attr><key>Version:</key><value>${pkg.version ||
                    'No version'}</value></attr>
            </item>`,
            )
            .join('')}
    </list>
    `;
