const regex = /#(\d*)\(PR-(\d*)\)/;

export default ({ rawTags = [] } = {}) =>
    rawTags
        .map(tag => tag.match(regex) || [])
        .filter(matches => matches.length)
        .map(([tag, releaseNumber, pullRequestNumber]) => ({
            tag,
            releaseNumber,
            pullRequestNumber,
        }));
