import { input, cjsOutput, postInfo, plugins } from '@kikd/rollup-utils';

export default {
    filter: pkg => pkg.bin && pkg.bin.constructor === Object,
    config: ({ baseConfig, dependencies }) => p =>
        Object.values(p.bin)
            .map(file => ({ ...p, file }))
            .map(pkg => ({
                type: 'npm-cli-tool',
                in: input({
                    pkg,
                    dependencies,
                    plugins: plugins({ executable: true }),
                }),
                out: [cjsOutput({ pkg, banner: '#!/usr/bin/env node' })],
                post: postInfo({ pkg, baseConfig, dependencies }),
            })),
};
