import { resolve } from 'path'

import build from '@kikd/build-rollup'
import postBuild from '@kikd/post-build'
import rollup, { commonPlugins } from '@kikd/rollup-utils'

const type = 'npm-package'
const rollupConfig = { type, plugins: commonPlugins, format: 'cjs' }

export default {
    type,
    filter: ({ srcDir = '.', files = [] }) => {
        if (!files.includes('package.json')) return false
        // eslint-disable-next-line global-require, import/no-dynamic-require
        const manifest = require(resolve(srcDir, 'package.json'))
        return !manifest.bin && (manifest.main || files.includes('index.js'))
    },
    config: snowball =>
        rollup({
            ...rollupConfig,
            ...snowball,
            file: snowball.manifest.main || 'index.js',
        }),
    build,
    postBuild,
}
