import fs from 'fs'
import pick from 'lodash.pick'
import { resolve } from 'path'
import jsonFile from 'jsonfile'
import promisify from 'es6-promisify'

const copy = promisify(fs.copyFile)
const writeJson = promisify(jsonFile.writeFile)
const requiredProps = [
    'description',
    'homepage',
    'author',
    'repository',
    'bugs',
    'license',
]

export default ({ root, baseConfig, config }) =>
    Promise.all([
        copy(
            resolve(root, 'LICENSE'),
            resolve(root, config.destDir, 'LICENSE')
        ),
        copy(
            resolve(root, config.srcDir, 'readme.md'),
            resolve(root, config.destDir, 'readme.md')
        ),
        writeJson(
            resolve(root, config.destDir, 'package.json'),
            {
                name: config.name,
                ...config.packageConfig,
                ...pick(baseConfig, requiredProps),
                dependencies: config.dependencyVersions,
            },
            { spaces: 4 }
        ),
    ])
