import { prompt } from 'inquirer'

export default ({ plugins = {} } = {}) => () =>
    prompt([
        {
            type: 'list',
            name: 'type',
            message: 'What kind of package would you like to create?',
            choices: Object.values(plugins)
                .filter(plugin => 'create' in plugin)
                .map(({ type }) => type),
        },
    ]).then(({ type }) => type)
