export default (packages = []) =>
    packages.map(pkg => {
        if (!pkg.startsWith('@') && pkg.includes('/')) return pkg.split('/')[0]

        const numOfSlashes = (pkg.match(/\//g) || []).length
        if (pkg.startsWith('@') && numOfSlashes > 1) {
            const split = pkg.split('/')
            return `${split[0]}/${split[1]}`
        }

        return pkg
    })
