export default ({ configs }) => `
    <bar>-</bar>
    <h1>Packages to be Built</h1>
    <bar>-</bar>
    <list>
        ${[]
            .concat(...Object.values(configs))
            .map(
                ({ post: { name, srcDir, destDir } = {}, type } = {}) => `
                    <item>
                        <title>${name} (${type})</title>
                        <details>${srcDir} -> ${destDir}</details>
                    </item>
                `
            )
            .join('')}
    </list>
    `
