export default checks => `
    <bar>-</bar>
    <h1>Checked Packages</h1>
    <bar>-</bar>
    <list>
        ${Object.entries(checks.packages)
            .map(
                ([name, pkgChecks]) => `<item>
                <title>${name}</title>
                ${Object.entries(pkgChecks)
                    .filter(([check]) => check !== 'publishable')
                    .map(
                        ([check, result]) =>
                            result
                                ? `<check><success>${check}</success></check>`
                                : `<check><fail>${check}</fail></check>`,
                    )
                    .join('')}
                <check>${Array(
                    Object.keys(pkgChecks).reduce(
                        (a, b) => (a.length > b.length ? a : b),
                        0,
                    ).length + 3,
                ).join('â”€')}</check>
                <check>${
                    pkgChecks.publishable
                        ? '<success>publishable</success>'
                        : '<fail>not publishable</fail>'
                }</check>
            </item>`,
            )
            .join('')}
    </list>
    <bar>-</bar>
    <title>Repository</title>
    ${Object.entries(checks.root)
        .map(
            ([check, result]) =>
                result
                    ? `<check><success>${check}</success></check>`
                    : `<check><fail>${check}</fail></check>`,
        )
        .join('')}
    `;
