export default packages => `
    <bar>-</bar>
    <h1>Checked Packages</h1>
    <bar>-</bar>
    <list>
        ${packages
            .map(
                pkg => `<item>
                <title>${pkg.name}</title>
                <check>${
                    pkg.publishable
                        ? '<success>publishable</success>'
                        : '<fail>not publishable</fail>'
                }</check>
            </item>`,
            )
            .join('')}
    </list>
    `;
