export default () => ({ packageNames, dependencies }) => `
    <bar>-</bar>
    <h1>Package Dependencies</h1>
    <bar>-</bar>
    <list>
        ${packageNames
            .map(
                name => `<item>
                <title>${name}</title>
                <attr><key>Local:</key><value>${dependencies[name].local.join(
                    ', '
                ) || '--- None ---'}</value></attr>
                <attr><key>External:</key><value>${dependencies[
                    name
                ].external.join(', ') || '--- None ---'}</value></attr>
            </item>`
            )
            .join('')}
    </list>
    `
