export default ({ packages, dependencies }) => `
    <bar>-</bar>
    <h1>Package Dependencies</h1>
    <bar>-</bar>
    <list>
        ${packages
            .map(
                pkg => `<item>
                <title>${pkg.name}</title>
                <attr><key>Local:</key><value>${dependencies[pkg.name]
                    .filter(dep => dep in dependencies)
                    .join(', ') || '--- None ---'}</value></attr>
                <attr><key>External:</key><value>${dependencies[pkg.name]
                    .filter(dep => !(dep in dependencies))
                    .join(', ') || '--- None ---'}</value></attr>
            </item>`,
            )
            .join('')}
    </list>
    `;
