const NONE = '--- None ---'
const formatPackage = dependencies => name => `<item>
    <title>${name}</title>
    <attr>
        <key>Local:</key>
        <value>${dependencies[name].local.join(', ') || NONE}</value>
    </attr>
    <attr>
        <key>External:</key>
        <value>${dependencies[name].external.join(', ') || NONE}</value>
    </attr>
</item>`

export default () => ({ packageNames, dependencies }) => `
    <bar>-</bar>
    <h1>Package Dependencies</h1>
    <bar>-</bar>
    <list>${packageNames.map(formatPackage(dependencies)).join('')}</list>
`
