const formatBoolean = (bool, check) =>
    bool ? `<success>${check}</success>` : `<fail>${check}</fail>`

export default ({ dry }) => ({
    monorepoName,
    root,
    isGitDir,
    packageJsonExists,
    modulesDirExists,
}) => `
    <bar>-</bar>
    <h1>[${monorepoName}] Monorepo Initialization</h1>
    <bar>-</bar>
    <h1>Directory Root: <details>${root}</details></h1>
    <list>
        <bullet>${formatBoolean(isGitDir, 'Is a Git Directory')}</bullet>
        <bullet>${formatBoolean(
            packageJsonExists,
            'Has a package.json'
        )}</bullet>
        <bullet>${formatBoolean(
            modulesDirExists,
            'Has a src/node_modules directory'
        )}</bullet>
    </list>
    ${
        dry
            ? `
                <bar>-</bar>
                <h1><warning>DRY RUN... nothing created</warning></h1>
                <bar>-</bar>
              `
            : ''
    }`
