const check = (bool, msg) =>
    bool ? `<success>${msg}</success>` : `<fail>${msg}</fail>`

const dryMsg = `
    <bar>-</bar>
    <h1><warning>DRY RUN... nothing created</warning></h1>
    <bar>-</bar>
`

export default ({ dry }) => ({
    monorepoName,
    root,
    isGitDir,
    packageJsonExists,
    modulesDirExists,
}) => `
    <bar>-</bar>
    <h1>[${monorepoName}] Monorepo Initialization</h1>
    <bar>-</bar>
    <h1>Directory Root: <details>${root}</details></h1>
    <list>
        <bullet>
            ${check(isGitDir, 'Is a Git Directory')}
        </bullet>
        <bullet>
            ${check(packageJsonExists, 'Has a package.json')}
        </bullet>
        <bullet>
            ${check(modulesDirExists, 'Has a src/node_modules directory')}
        </bullet>
    </list>
    ${dry ? dryMsg : ''}
`
