export default () => ({ packageNames = [], manifests = {} }) => `
    <bar>-</bar>
    <h1>Package List</h1>
    <bar>-</bar>
    <list>
        ${packageNames
            .map(
                pkg => `<item>
                <attr><key>Name:</key><value>${pkg}</value></attr>
                <attr><key>Version:</key><value>${manifests[pkg].version ||
                    'No version'}</value></attr>
            </item>`
            )
            .join('')}
    </list>
    `
