export default ({ publishable, destination, publishing }) => `
    <bar>-</bar>
    <h1>Publishable Packages</h1>
    <bar>-</bar>
    <list>
        ${publishable
            .map(
                ({ name }) => `<item>
                    <check>
                        ${
                            publishing.includes(name)
                                ? `<success>${name}</success>`
                                : `<fail>${name}</fail>`
                        }
                        <attr><key>NPM:</key><value>${
                            destination[name].destVersion
                        }</value></attr>
                        <attr><key>Next:</key><value>${
                            destination[name].srcVersion
                        }</value></attr>
                    </check>
                </item>`,
            )
            .join('')}
    </list>
`;
