export default ({ publishable, destination }) => `
    <bar>-</bar>
    <h1>Publishable Packages</h1>
    <bar>-</bar>
    <list>
        ${publishable
            .map(
                ({ name }) => `<item>
                    <check>
                        <success>${name}</success>
                        <attr><key>NPM:</key><value>${
                            destination[name].destVersion
                        }</value></attr>
                        <attr><key>Next:</key><value>${
                            destination[name].srcVersion
                        }</value></attr>
                    </check>
                </item>`,
            )
            .join('')}
    </list>
`;
