export default ({ updates }) => `
    <bar>-</bar>
    <h1>Packages to be updated</h1>
    <bar>-</bar>
    <list>
        ${Object.entries(updates)
            .map(
                ([name, { old, new: version, type }]) => `<item>
                <title>${name} - ${type}</title>
                <check>${old} -> ${version}</check>
            </item>`,
            )
            .join('')}
    </list>
    `;
