import toCamelCase from 'to-camel-case'

export default ({ args = {}, conversions = {} }) =>
    Object.entries(args)
        .map(([key, value]) => [key !== '_' ? toCamelCase(key) : key, value])
        .map(
            ([key, value]) =>
                key in conversions ? [conversions[key], value] : [key, value]
        )
        .reduce((obj, pair) => ({ ...obj, [pair[0]]: pair[1] }), {})
