import describe from 'kape'
import convertArgs from '@kli-tool/convert-args'

describe('Argument Converter', convertArgs, snapshot =>
    snapshot(
        [{}],
        [{ args: { _: 'untouched' } }],
        [{ args: { _: 'converted' }, conversions: { _: 'convert' } }],
        [{ args: { 'with-dash': 'value' } }],
        [
            {
                args: { 'with-dash': 'value' },
                conversions: { withDash: 'convertedDash' },
            },
        ]
    )
)
