import configure from '@kli-tool/executor';
import describe from 'tape-bdd';
import 'loud-rejection/register';
import withBlueTape from 'blue-tape';

const command = c => ({
    searcher: {
        get: () => [c],
    },
});

describe(
    'KLI Executor',
    it => {
        it('should have an execute command', assert => {
            const executor = configure(
                command({
                    name: 'test',
                    run: () => assert.pass(),
                }),
            );
            executor(['test']);
        });
    },
    withBlueTape,
);
