import * as R from 'ramda'

export default (obj = {}, path = '') =>
    R.pipe(
        R.always(obj),
        R.compose(
            R.view,
            R.lensPath,
            R.split('.'),
            R.unless(R.is(String), R.always(''))
        )(path),
        R.defaultTo(obj),
        R.keys
    )()
