import describe from 'kape'
import getKeysWithPath from '@kli-tool/get-keys-with-path'

const obj = {
    top: 'roar',
    topTwo: 'again',
    topDeep: {
        mid: 'roar',
        midTwo: 'again',
        midDeep: {
            bottom: 'roar',
            bottomTwo: 'again',
        },
    },
}

describe('@kli-tool/get-keys-with-path', getKeysWithPath, snapshot => {
    snapshot('Extract path', [
        [],
        [obj],
        [obj, 'top'],
        [obj, 'topDeep'],
        [obj, 'topDeep.mid'],
        [obj, 'topDeep.midDeep'],
        [obj, 'topDeep.midDeep.bottom'],
    ])

    snapshot('Bad Arguments', [
        [null],
        [null, null],
        ['', ''],
        ['.', '.'],
        [5, 5],
        [{}, {}],
        [() => {}, () => {}],
    ])
})
