import TS from 'trie-search';

export default ({ commands = [] } = {}) => {
    const parsedCommands = commands.map(command => ({
        ...command,
        tokens: [command.name, command.alias].join(' '),
    }));
    const searcher = new TS(['tokens']);
    searcher.addAll(parsedCommands);
    return searcher;
};
