<template>
    <altar ref="altar">
        <altar-table ref="table"></altar-table>
    </altar>
</template>

<script>
import '@babel/polyfill'
import { altar } from 'runic'
import portfolio from './portfolio.js'
import blueprint, { table } from '@runic-unreal/blueprint'
import runes from '@runic-ramda/runes'

export default {
    name: 'App',
    components: { altar, 'altar-table': table },
    mounted() {
        this.$refs.altar
            .init({
                version: 'portfolio@0.0.0',
                system: blueprint({ background: this.$refs.table.$el }),
                family: portfolio,
                runes,
            })
            .then(({ altar }) => altar.trigger('zoomAt'))
    },
}
</script>

<style lang="sass">
$number-color: #A2FD42
$function-color: #FD393B

.app
    height: 500px
    width: 500px

.connection path
    fill: none
    stroke: #eee
    stroke-width: 2.5px !important
    pointer-events: none

    &.active
        stroke: #aaa

.socket-input-number path
    stroke: $number-color !important

.socket-input-function path
    stroke: $function-color !important
</style>
