import give from '@fever/give'
import pick from '@fever/pick'
import code from '@fever/code'
import enter from '@fever/enter'
import newCode from '@fever/new-code'
import onlyTags from '@fever/only-tags'
import clearCode from '@fever/clear-code'
import newEntries from '@fever/new-entries'
import moreEntries from '@fever/more-entries'
import giveEntries from '@fever/give-entries'
import createModerator from '@rampant/moderator'
import cancelGiveaway from '@fever/cancel-giveaway'

export default ({ moderator: chat, db, prefix, logger }) => {
    const moderator = createModerator({
        chat,
        db,
        prefix,
        streamer: 'raybenefield',
    })
    give({ moderator, db, prefix, logger })
    enter({ moderator, db, prefix, logger })
    pick({ moderator, db, prefix, logger })
    code({ moderator, db, prefix, logger })
    newCode({ moderator, db, prefix, logger })
    onlyTags({ moderator, db, prefix, logger })
    clearCode({ moderator, db, prefix, logger })
    newEntries({ moderator, db, prefix, logger })
    moreEntries({ moderator, db, prefix, logger })
    giveEntries({ moderator, db, prefix, logger })
    cancelGiveaway({ moderator, db, prefix, logger })
}
