import give from '@fever/give'
import pick from '@fever/pick'
import enter from '@fever/enter'
import code from '@fever/code'
import createModerator from '@fever/moderator'

export default ({ moderator: chat, db, prefix, logger }) => {
    const moderator = createModerator({ chat, streamer: 'raybenefield' })
    give({ moderator, db, prefix, logger })
    enter({ moderator, db, prefix, logger })
    pick({ moderator, db, prefix, logger })
    code({ moderator, db, prefix, logger })
}
