import { white, green, grey } from 'chalk'

// eslint-disable-next-line import/no-mutable-exports
export let testCount = 0
const success = msg => {
    testCount += 1
    return `    ${white(`#${testCount}`.padEnd(3, ' '))} ${green('âœ”')} ${grey(
        msg
    )}`
}

