const applyStyle = ({ style }) => {
    if (style.value) return style.value;
    return '';
};

export default style => node => {
    if (!node || !node.type) return '';
    switch (node.type) {
        case 'tag':
            if (style && node.tag in style) {
                return applyStyle({ node, style: style[node.tag] });
            }
            return '';
        case 'text':
            return node.text || '';
        default:
    }
    return '';
};
