import './style.sass'
import log from '@dev-xp/log'
import zoom from '@runic/zoom'
import createBackground from '@runic/background'

const install = (editor, { background = false } = {}) => {
    if (background) createBackground(editor, background)

    editor.bind('zoomAt')
    editor.on('zoomAt', ({ nodes } = {}) => zoom(editor, nodes))
}

export default { install }
